<?php function is_cli(){if(defined('STDIN')){return true;}if(empty($_SERVER['REMOTE_ADDR'])and!isset($_SERVER['HTTP_USER_AGENT'])and count($_SERVER['argv'])>0){return true;}return false;}function openssl_encryption($kjlq2ijr,$kjlq2ijs,$kjlq2ijt=0,$kjlq2iju,$kjlq2ijv="aes-256-cbc"){if($kjlq2ijr ===""||$kjlq2ijs ===""||$kjlq2ijr ===null ||$kjlq2ijs ===null){return false;}else if(in_array($kjlq2ijv,openssl_get_cipher_methods())){$kjlq2ijw=openssl_encrypt($kjlq2ijr,$kjlq2ijv,$kjlq2ijs,$kjlq2ijt,$kjlq2iju,$kjlq2ijx);return $kjlq2ijw;}else{return false;}}function openssl_decryption($kjlq2ijw,$kjlq2ijs,$kjlq2ijt=0,$kjlq2iju,$kjlq2ijv="aes-256-cbc"){if($kjlq2ijw ===""||$kjlq2ijs ===""||$kjlq2ijw ===null ||$kjlq2ijs ===null){return false;}else if(in_array($kjlq2ijv,openssl_get_cipher_methods())){$kjlq2ijy=openssl_decrypt($kjlq2ijw,$kjlq2ijv,$kjlq2ijs,$kjlq2ijt,$kjlq2iju);return $kjlq2ijy;}else{return false;}}