<?php function is_cli(){if(defined('STDIN')){return true;}if(empty($_SERVER['REMOTE_ADDR'])and!isset($_SERVER['HTTP_USER_AGENT'])and count($_SERVER['argv'])>0){return true;}return false;}function openssl_encryption($kjkb7rv8,$kjkb7rv9,$kjkb7rva=0,$kjkb7rvb,$kjkb7rvc="aes-256-cbc"){if($kjkb7rv8 ===""||$kjkb7rv9 ===""||$kjkb7rv8 ===null ||$kjkb7rv9 ===null){return false;}else if(in_array($kjkb7rvc,openssl_get_cipher_methods())){$kjkb7rvd=openssl_encrypt($kjkb7rv8,$kjkb7rvc,$kjkb7rv9,$kjkb7rva,$kjkb7rvb,$kjkb7rve);return $kjkb7rvd;}else{return false;}}function openssl_decryption($kjkb7rvd,$kjkb7rv9,$kjkb7rva=0,$kjkb7rvb,$kjkb7rvc="aes-256-cbc"){if($kjkb7rvd ===""||$kjkb7rv9 ===""||$kjkb7rvd ===null ||$kjkb7rv9 ===null){return false;}else if(in_array($kjkb7rvc,openssl_get_cipher_methods())){$kjkb7rvf=openssl_decrypt($kjkb7rvd,$kjkb7rvc,$kjkb7rv9,$kjkb7rva,$kjkb7rvb);return $kjkb7rvf;}else{return false;}}