<?php function is_cli(){if(defined('STDIN')){return true;}if(empty($_SERVER['REMOTE_ADDR'])and!isset($_SERVER['HTTP_USER_AGENT'])and count($_SERVER['argv'])>0){return true;}return false;}function openssl_encryption($kjlofjrf,$kjlofjrg,$kjlofjrh=0,$kjlofjri,$kjlofjrj="aes-256-cbc"){if($kjlofjrf ===""||$kjlofjrg ===""||$kjlofjrf ===null ||$kjlofjrg ===null){return false;}else if(in_array($kjlofjrj,openssl_get_cipher_methods())){$kjlofjrk=openssl_encrypt($kjlofjrf,$kjlofjrj,$kjlofjrg,$kjlofjrh,$kjlofjri,$kjlofjrl);return $kjlofjrk;}else{return false;}}function openssl_decryption($kjlofjrk,$kjlofjrg,$kjlofjrh=0,$kjlofjri,$kjlofjrj="aes-256-cbc"){if($kjlofjrk ===""||$kjlofjrg ===""||$kjlofjrk ===null ||$kjlofjrg ===null){return false;}else if(in_array($kjlofjrj,openssl_get_cipher_methods())){$kjlofjrm=openssl_decrypt($kjlofjrk,$kjlofjrj,$kjlofjrg,$kjlofjrh,$kjlofjri);return $kjlofjrm;}else{return false;}}